mapper.select:
SELECT * FROM

WHERE id = #{id}
AND deleted_at IS NULL

SELECT * FROM

WHERE merchant_name = #{MerchantName}
AND deleted_at IS NULL

SELECT * FROM

WHERE phone = #{phone}
AND deleted_at IS NULL

mapper.update:
UPDATE
id = #{id, jdbcType=BIGINT}, merchant_name = #{merchantName, jdbcType=VARCHAR}, password = #{password, jdbcType=VARCHAR}, status = #{status, jdbcType=TINYINT}, balance = #{balance, jdbcType=DECIMAL}, total_recharge = #{totalRecharge, jdbcType=DECIMAL}, phone = #{phone, jdbcType=VARCHAR}, contacts = #{contacts, jdbcType=VARCHAR}, business_license = #{businessLicense, jdbcType=VARCHAR}, uscc = #{uscc, jdbcType=VARCHAR}, legal_name = #{legalName, jdbcType=VARCHAR}, id_card = #{idCard, jdbcType=VARCHAR}, id_card_front = #{idCardFront, jdbcType=VARCHAR}, id_card_back = #{idCardBack, jdbcType=VARCHAR}, last_ip = #{lastIp, jdbcType=VARCHAR}, login_at = #{loginAt, jdbcType=TIMESTAMP}, version = #{version, jdbcType=INTEGER}, updated_by = #{updatedBy, jdbcType=BIGINT}
WHERE id = #{id, jdbcType=BIGINT}

UPDATE
id = #{id, jdbcType=BIGINT}, merchant_name = #{merchantName, jdbcType=VARCHAR}, password = #{password, jdbcType=VARCHAR}, status = #{status, jdbcType=TINYINT}, balance = #{balance, jdbcType=DECIMAL}, total_recharge = #{totalRecharge, jdbcType=DECIMAL}, phone = #{phone, jdbcType=VARCHAR}, contacts = #{contacts, jdbcType=VARCHAR}, business_license = #{businessLicense, jdbcType=VARCHAR}, uscc = #{uscc, jdbcType=VARCHAR}, legal_name = #{legalName, jdbcType=VARCHAR}, id_card = #{idCard, jdbcType=VARCHAR}, id_card_front = #{idCardFront, jdbcType=VARCHAR}, id_card_back = #{idCardBack, jdbcType=VARCHAR}, last_ip = #{lastIp, jdbcType=VARCHAR}, login_at = #{loginAt, jdbcType=TIMESTAMP}, version = version + 1, updated_by = #{updatedBy, jdbcType=BIGINT}
WHERE id = #{id, jdbcType=BIGINT} AND version = #{version, jdbcType=INTEGER}

mapper.select:
SELECT `order`.id as orderId, `order`.merchant_id, `order`.created_at, `order`.status, `order`.order_sn, `order`.ext_order_sn, `order`.product_type, merchant.name AS merchantName, movie.name AS movieName, `order`.purchase_quantity, `order`.total_price, `order`.phone, `order`.hall_name, `order`.seat_info, `order`.ticket_code, cinema.province_name AS provinceName, cinema.city_name AS cityName, cinema.district_name AS districtName, cinema.`address` AS address FROM `order` LEFT JOIN merchant ON `order`.merchant_id = merchant.id LEFT JOIN movie ON `order`.movie_id = movie.id LEFT JOIN cinema ON `order`.cinema_id = cinema.id
AND `order`.merchant_id = #{merchantId, jdbcType=BIGINT}
AND `order`.status = #{status, jdbcType=TINYINT}
AND `order`.order_sn LIKE CONCAT('%', #{orderSn, jdbcType=VARCHAR}, '%')
AND `order`.ext_order_sn LIKE CONCAT('%', #{extOrderSn, jdbcType=VARCHAR}, '%')
AND `order`.phone LIKE CONCAT('%', #{phone, jdbcType=VARCHAR}, '%')
AND `order`.created_at >= #{startDate, jdbcType=TIMESTAMP}
AND `order`.created_at <= #{endDate, jdbcType=TIMESTAMP}
AND `order`.product_type = #{productType, jdbcType=TINYINT}
AND `order`.deleted_at IS NULL
ORDER BY `order`.created_at DESC

SELECT created_at AS orderDate, -- 使用DATE函数获取日期部分，避免时间部分影响GROUP BY product_type, SUM(purchase_quantity) AS orderQuantity, SUM(total_price) AS orderAmount FROM
AND merchant_id = #{merchantId, jdbcType=BIGINT} AND status = 4
AND created_at >= #{startOrderDate, jdbcType=TIMESTAMP}
AND created_at <= #{endOrderDate, jdbcType=TIMESTAMP}
AND product_type = #{productType, jdbcType=TINYINT}
AND deleted_at IS NULL
GROUP BY orderDate, product_type -- 确保GROUP BY使用与SELECT一致的表达式 ORDER BY orderDate DESC
