mapper.select:
SELECT `order`.id, `order`.status, `order`.purchase_quantity AS purchaseQuantity, `order`.total_price as totalPrice, `order`.stop_show_time AS stopShowTime, `order`.created_at as createdAt, `order`.updated_at as updatedAt, cinema.name AS cinemaName, movie.name AS movieName, movie.poster_url AS posterUrl, `show`.hall_name AS hallName, `show`.show_time AS showTime FROM `order` LEFT JOIN `user` ON `order`.user_id = `user`.id LEFT JOIN supplier ON `order`.supplier_id = supplier.id LEFT JOIN cinema ON `order`.cinema_id = cinema.id LEFT JOIN movie ON `order`.movie_id = movie.id LEFT JOIN `show` ON `order`.show_id = `show`.id
AND `user`.id = #{userId}
AND `order`.platform = #{platform}
#{item}
AND `order`.order_type = 1 AND `order`.deleted_at IS NULL
ORDER BY `order`.created_at DESC

SELECT `order`.id, `order`.order_sn AS orderSn, `order`.phone, `order`.status, `order`.purchase_quantity AS purchaseQuantity, `order`.seat_info AS seatInfo, `order`.ticket_code AS ticketCode, `order`.total_price AS totalPrice, `order`.official_price AS officialPrice, `order`.stop_show_time AS stopShowTime, `order`.created_at AS createdAt, cinema.name AS cinemaName, cinema.province_name AS provinceName, cinema.city_name AS cityName, cinema.district_name AS districtName, cinema.address, cinema.lng, cinema.lat, cinema.phone AS cinemaPhone, movie.name AS movieName, movie.poster_url AS posterUrl, `show`.hall_name AS hallName, `show`.duration, `show`.show_time AS showTime, `show`.show_time AS showDate, `show`.show_version_type AS showVersionType, `show`.stop_sell_time AS stopSellTime FROM `order` LEFT JOIN cinema ON `order`.cinema_id = cinema.id LEFT JOIN movie ON `order`.movie_id = movie.id LEFT JOIN `show` ON `order`.show_id = `show`.id
AND `order`.id = #{id}
AND `order`.user_id = #{userId}
AND `order`.deleted_at IS NULL
LIMIT 1

