mapper.select:
SELECT id as goodsId, group_id, goods_name, goods_logo, stock, goods_price, market_price, goods_status, goods_type FROM
group_id = #{groupId, jdbcType=BIGINT}
AND goods_type = #{goodsType, jdbcType=INTEGER}
AND deleted_at IS NULL

SELECT vGoods.`id` as goodsId, vGoods.supplier_id, vGoods.group_id, vGoods.goods_name, vGoods.`stock`, vGoods.old_goods_price, vGoods.goods_price, vGoods.old_market_price, vGoods.market_price, vGoods.goods_status, vGoods.goods_type FROM vGoods
AND vGoods.id = #{goodsId, jdbcType=BIGINT}
AND vGoods.group_id = #{groupId, jdbcType=BIGINT}
AND vGoods.goods_name LIKE CONCAT('%', #{goodsName, jdbcType=VARCHAR}, '%')
AND vGoods.goods_status = #{goodsStatus, jdbcType=INTEGER}
AND vGoods.goods_type = #{goodsType, jdbcType=INTEGER}
AND vGoods.goods_price > vGoods.old_goods_price
AND vGoods.old_goods_price > vGoods.goods_price
AND vGoods.goods_price = vGoods.old_goods_price
AND vGoods.deleted_at IS NULL

SELECT vGoods.*, vGroup.group_name, vGroup.brand_id, vGroup.brand_name FROM  vGoods
LEFT JOIN  vGroup ON vGoods.group_id = vGroup.id


        AND vGoods.id = #{id, jdbcType=BIGINT}


        AND vGoods.tp_goods_id = #{tpGoodsId, jdbcType=BIGINT}


        AND vGoods.group_id = #{groupId, jdbcType=BIGINT}


        AND vGoods.tp_group_id = #{tpGroupId, jdbcType=BIGINT}


        AND vGroup.brand_id = #{brandId, jdbcType=BIGINT}


        AND vGoods.goods_status = #{goodsStatus, jdbcType=INTEGER}


        AND vGoods.goods_type = #{goodsType, jdbcType=INTEGER}




                AND vGoods.goods_price > vGoods.old_goods_price


                AND vGoods.old_goods_price > vGoods.goods_price


                AND vGoods.goods_price = vGoods.old_goods_price




        AND vGoods.goods_name LIKE CONCAT('%', #{goodsName, jdbcType=VARCHAR}, '%')


        AND vGroup.group_name LIKE CONCAT('%', #{groupName, jdbcType=VARCHAR}, '%')


        AND vGroup.brand_name LIKE CONCAT('%', #{brandName, jdbcType=VARCHAR}, '%')

    AND vGoods.deleted_at IS NULL

ORDER BY vGoods.group_id DESC

SELECT vGoods.*, vGroup.group_name, vGroup.brand_id, vGroup.brand_name FROM  vGoods
LEFT JOIN  vGroup ON vGoods.group_id = vGroup.id


        AND vGoods.id = #{id, jdbcType=BIGINT}


        AND vGoods.goods_status = #{goodsStatus, jdbcType=INTEGER}


        AND vGoods.goods_type = #{goodsType, jdbcType=INTEGER}


        AND vGoods.goods_name LIKE CONCAT('%', #{goodsName, jdbcType=VARCHAR}, '%')


        AND vGroup.group_name LIKE CONCAT('%', #{groupName, jdbcType=VARCHAR}, '%')


        AND vGroup.brand_name LIKE CONCAT('%', #{brandName, jdbcType=VARCHAR}, '%')

    AND vGoods.deleted_at IS NULL

ORDER BY vGoods.group_id DESC

mapper.update:
UPDATE
supplier_id = #{supplierId, jdbcType=BIGINT}, tp_goods_id = #{tpGoodsId, jdbcType=BIGINT}, `goods_name` = #{goodsName, jdbcType=VARCHAR}, group_id = #{groupId, jdbcType=BIGINT}, tp_group_id = #{tpGroupId, jdbcType=BIGINT}, stock = #{stock, jdbcType=INTEGER}, old_goods_price = #{oldGoodsPrice, jdbcType=DECIMAL}, goods_price = #{goodsPrice, jdbcType=DECIMAL}, old_market_price = #{oldMarketPrice, jdbcType=DECIMAL}, market_price = #{marketPrice, jdbcType=DECIMAL}, goods_status = #{goodsStatus, jdbcType=TINYINT}, goods_logo = #{goodsLogo, jdbcType=VARCHAR}, goods_type = #{goodsType, jdbcType=TINYINT}, min_buy = #{minBuy, jdbcType=INTEGER}, max_buy = #{maxBuy, jdbcType=INTEGER}, details = #{details, jdbcType=VARCHAR}, updated_by = #{updatedBy, jdbcType=BIGINT}
WHERE id = #{id, jdbcType=BIGINT}

UPDATE 
SET deleted_at = NOW(),
deleted_by = #{deleterId, jdbcType=BIGINT}
WHERE id = #{id, jdbcType=BIGINT}
