sql.where:

    AND vo.vip_goods_id = #{vipGoodsId, jdbcType=BIGINT}


    AND vo.status = #{status, jdbcType=BIGINT}


    AND vo.refund_status = #{refundStatus, jdbcType=BIGINT}


    AND vo.created_at  >=  #{startDate, jdbcType=TIMESTAMP}


    AND vo.created_at  <=  #{endDate, jdbcType=TIMESTAMP}


    AND vo.order_sn LIKE CONCAT('%', #{orderSn, jdbcType=VARCHAR}, '%')


    AND vo.ext_order_sn LIKE CONCAT('%', #{extOrderSn, jdbcType=VARCHAR}, '%')


    AND vo.phone LIKE CONCAT('%', #{phone, jdbcType=VARCHAR}, '%')


    AND m.merchant_name LIKE CONCAT('%', #{merchantName, jdbcType=VARCHAR}, '%')


    AND vo.vip_goods_name LIKE CONCAT('%', #{vipGoodsName, jdbcType=VARCHAR}, '%')

AND vo.deleted_at IS NULL

mapper.update:
UPDATE
order_sn = #{orderSn, jdbcType=VARCHAR}, ext_order_sn = #{extOrderSn, jdbcType=VARCHAR}, status = #{status, jdbcType=TINYINT}, refund_status = #{refundStatus, jdbcType=TINYINT}, receipt = #{receipt, jdbcType=VARCHAR}, refund_receipt = #{refundReceipt, jdbcType=VARCHAR}, total_price = #{totalPrice, jdbcType=DECIMAL}, official_price = #{officialPrice, jdbcType=DECIMAL}, settlement_price = #{settlementPrice, jdbcType=DECIMAL}, max_money = #{maxMoney, jdbcType=DECIMAL}, refund_money = #{refundMoney, jdbcType=DECIMAL}, merchant_id = #{merchantId, jdbcType=BIGINT}, vip_goods_id = #{vipGoodsId, jdbcType=BIGINT}, phone = #{phone, jdbcType=VARCHAR}, buy_num = #{buyNum, jdbcType=INTEGER}, card_list = #{cardList, jdbcType=VARCHAR}, `vip_goods_name` = #{vipGoodsName, jdbcType=VARCHAR}, vip_goods_type = #{vipGoodsType, jdbcType=TINYINT}, supplier_id = #{supplierId, jdbcType=BIGINT}, supplier_order_sn = #{supplierOrderSn, jdbcType=VARCHAR}, settle_status = #{settleStatus, jdbcType=TINYINT}, order_response_body = #{orderResponseBody, jdbcType=VARCHAR}, updated_by = #{updatedBy, jdbcType=BIGINT}
WHERE id = #{id, jdbcType=BIGINT}

UPDATE 
SET deleted_at = NOW(),
deleted_by = #{deleterId, jdbcType=BIGINT}
WHERE id = #{id, jdbcType=BIGINT}
